import Button from '@material-ui/core/Button';
var fetch = require('node-fetch');
import { useState } from 'react';

const Buttons = () => {

    const [running, setRunning] = useState(false);

    const sendStart = () => {
        fetch('http://127.0.0.1:5000/start_optimizer', 
		{headers: {'Content-Type': 'application/json'},
		method: 'POST'
        }).then(function (response) {
            return response.text();
        })
    }

    const sendStop = () => {
        fetch('http://127.0.0.1:5000/stop_optimizer', 
		{headers: {'Content-Type': 'application/json'},
		method: 'POST'
        }).then(function (response) {
            return response.text();
        })
    }

    const handleStart = () => {
        setRunning(true);
        sendStart();
    }

    const handleStop = () => {
        setRunning(false);
        sendStop();
    }

    return (
        <div className="startstopbuttons">
            <Button className='startbutton' variant="contained" color="primary" onClick={handleStart}>
                Optimize
            </Button>
            <Button className='stopbutton' variant="contained" disabled={!running} color="default" onClick={handleStop}>
                Stop
            </Button>
        </div>
    );
};

export default Buttons;
