import { useState } from 'react';
import { useEffect } from 'react';
import { useBetween } from 'use-between';
import { ShareablePopSizeState } from './PopSlider.js';
import { ShareableProbeParams } from './Buttons.js';  
import NodeFetch from 'node-fetch';

const endPointAddress = 'http://127.0.0.1:8000'; // TODO: Use environment variable

export const ShareableRunningState = () => {
    const [running, setRunning] = useState(false);
    return {
        running,
        setRunning
    };
};

export const ShareableFinishedState = () => {
    const [finished, setFinished] = useState(false);
    return {
        finished,
        setFinished
    };
};

export const ShareableOutputState = () => {
    const [output, setOutput] = useState(null);
    return {
        output,
        setOutput
    };    
};

export const ShareableWarningState = () => {
    const [warning, setWarning] = useState('');
    return {
        warning,
        setWarning
    };    
};

// Check whether the user has provided valid input and generate a warning to show
const screenInput = (probeParams) => {
    if (probeParams.params.temperature > 100.0 || probeParams.params.Temperature < 0.0) {
        return 'Temperature must be in the range 0-100C.';
    }
    if (probeParams.params.magnesium > 0.1) {
        return 'Magnesium must be in the range 0-100mM';
    }
    if (probeParams.params.sodium > 0.5 || probeParams.params.sodium < 0.01) {
        return 'Sodium must be in the range 10-500mM';
    }
    if (probeParams.WT.match(/[^GCTAgcta]/) || probeParams.SNP.match(/[^GCTAgcta]/)) {
        return 'Input sequences must be DNA, and must consist of A/C/T/G bases.';
    }
    if (probeParams.WT.length < 10 || probeParams.WT.length > 100) {
        return 'Sequence lengths must be in the range 0-100 bases.';
    }
    if (probeParams.SNP.length < 10 || probeParams.SNP.length > 100) {
        return 'Sequence lengths must be in the range 0-100 bases.';
    }
    return ''; // No warnings
}

async function runOptimizer(probeParams, popSize) {
    var endPoint = endPointAddress.concat('/start_optimizer');
    return NodeFetch(endPoint,
    {
        headers: { 'Content-Type': 'application/json' },
        method: 'POST',
        body: JSON.stringify({'probeParams': probeParams, 'popSize': popSize})
    }).then(response => {
        if(response.status >= 400) {
            throw new Error('Bad response from server')
        }
    }).then(function (response) {
        return response.json();
    })
};

const Optimizer = () => {

    const { probeParams } = useBetween(ShareableProbeParams);
    const { setRunning } = useBetween(ShareableRunningState);
    const { setFinished } = useBetween(ShareableFinishedState);
    const { setOutput } = useBetween(ShareableOutputState);
    const { popSize } = useBetween(ShareablePopSizeState);
    const { warning, setWarning } = useBetween(ShareableWarningState);

    // Run on mount
    useEffect(() => {
        let mounted = true
        if(mounted) {
            setWarning(screenInput(probeParams));
        }
        if(warning)
        runOptimizer(probeParams, popSize)
        .then(output => {
            if(success) {
                setOutput(output);
                setFinished(true);
                setRunning(false);
            }
        }).catch(err => {
            setFinished(false);
            setRunning(false);
            setWarning('An error occurred while connecting to the optimization server. Please try again.');
        })
        return () => mounted = false;
    // eslint-disable-next-line 
    }, []);

    return null;
};

export default Optimizer;
