import { Component } from 'react';
import { ShareableRunningState } from './Optimizer.js';
import { ShareableTemperatureState, ShareableSodiumState, ShareableMagnesiumState } from './ConditionEntry.js';
import { ShareableWTState, ShareableSNPState } from './SequenceEntry.js';
import Optimizer from './Optimizer.js';
import Button from '@material-ui/core/Button';
import { useBetween } from 'use-between';

const SharedState = () => {
    const { running, setRunning } = useBetween(ShareableRunningState);
    const { temperature } = useBetween(ShareableTemperatureState);
    const { sodium } = useBetween(ShareableSodiumState);
    const { magnesium } = useBetween(ShareableMagnesiumState);
    const { WT_seq } = useBetween(ShareableWTState);
    const { SNP_seq } = useBetween(ShareableSNPState);
    return {
        running, setRunning, temperature, sodium, magnesium, WT_seq, SNP_seq
    };
};

class Buttons extends Component {

    constructor() {
        var probe_params = {
            WT:SharedState.WT_seq,
            SNP:SharedState.SNP_seq,
            minlength:6,
            mut_rate:0.5,
            concentrations:{non_mut_target:1e-7, mut_target:1e-7, probeF:1e-7, probeQ:1e-7, sink:1e-7, sinkC:1e-7},
            params:{temperature:parseFloat(SharedState.temperature), sodium:parseFloat(SharedState.sodium)/1000.0, magnesium:parseFloat(SharedState.magnesium)/1000.0},
            beta:[0,0,0,0],
            truncations:[]
        };
        var props = { probe_params };
        this.optimizer = Optimizer(props);
    }

    handleStart = (event, newValue) => {
        var probe_params = {
            WT:SharedState.WT_seq,
            SNP:SharedState.SNP_seq,
            minlength:6,
            mut_rate:0.5,
            concentrations:{non_mut_target:1e-7, mut_target:1e-7, probeF:1e-7, probeQ:1e-7, sink:1e-7, sinkC:1e-7},
            params:{temperature:parseFloat(SharedState.temperature), sodium:parseFloat(SharedState.sodium)/1000.0, magnesium:parseFloat(SharedState.magnesium)/1000.0},
            beta:[0,0,0,0],
            truncations:[]
        };
        var props = { probe_params };
        this.optimizer = Optimizer(props);
        SharedState.setRunning(true);
        this.optimizer.run();
    };
    handleStop = (event, newValue) => {
        SharedState.setRunning(false);
        this.optimizer.reset();
    };

    render() {
        return (
            <div className="startstopbuttons">
                <Button className='startbutton' variant="contained" disabled={SharedState.running} color="primary" onClick={handleStart}>
                    Optimize
                </Button>
                <Button className='stopbutton' variant="contained" disabled={!SharedState.running} color="default" onClick={handleStop}>
                    Stop
                </Button>
            </div>
        )
    };

};

export default Buttons;
