import { Component } from 'react';
import { ShareableRunningState } from './Optimizer.js';
import { ShareableTemperatureState, ShareableSodiumState, ShareableMagnesiumState } from './ConditionEntry.js';
import { ShareableWTState, ShareableSNPState } from './SequenceEntry.js';
import Optimizer from './Optimizer.js';
import Button from '@material-ui/core/Button';
import { useBetween } from 'use-between';

class Buttons extends Component {

    constructor() {
        this.running, this.setRunning = useBetween(ShareableRunningState);
        this.temperature = useBetween(ShareableTemperatureState);
        this.sodium = useBetween(ShareableSodiumState);
        this.magnesium = useBetween(ShareableMagnesiumState);
        this.WT_seq = useBetween(ShareableWTState);
        this.SNP_seq = useBetween(ShareableSNPState);
        var probe_params = {
            WT:WT_seq,
            SNP:SNP_seq,
            minlength:6,
            mut_rate:0.5,
            concentrations:{non_mut_target:1e-7, mut_target:1e-7, probeF:1e-7, probeQ:1e-7, sink:1e-7, sinkC:1e-7},
            params:{temperature:parseFloat(this.temperature), sodium:parseFloat(this.sodium)/1000.0, magnesium:parseFloat(this.magnesium)/1000.0},
            beta:[0,0,0,0],
            truncations:[]
        };
        var props = { probe_params };
        this.optimizer = Optimizer(props);
    }

    handleStart = (event, newValue) => {
        var probe_params = {
            WT:WT_seq,
            SNP:SNP_seq,
            minlength:6,
            mut_rate:0.5,
            concentrations:{non_mut_target:1e-7, mut_target:1e-7, probeF:1e-7, probeQ:1e-7, sink:1e-7, sinkC:1e-7},
            params:{temperature:parseFloat(this.temperature), sodium:parseFloat(this.sodium)/1000.0, magnesium:parseFloat(this.magnesium)/1000.0},
            beta:[0,0,0,0],
            truncations:[]
        };
        var props = { probe_params };
        this.optimizer = Optimizer(props);
        this.setRunning(true);
        this.optimizer.run();
    };
    handleStop = (event, newValue) => {
        this.setRunning(false);
        this.optimizer.reset();
    };

    render() {
        return (
            <div className="startstopbuttons">
                <Button className='startbutton' variant="contained" disabled={running} color="primary" onClick={handleStart}>
                    Optimize
                </Button>
                <Button className='stopbutton' variant="contained" disabled={!running} color="default" onClick={handleStop}>
                    Stop
                </Button>
            </div>
        )
    };

};

export default Buttons;
